/**
Obtenir les éléments uniques consécutifs d'un tableau.

@returns Une fonction qui, lorsqu'elle est appelée, renvoie un élément aléatoire qui n'est jamais le même que le précédent.

@example
```
import uniqueRandomArray = require('unique-random-array');

const random = uniqueRandomArray([1, 2, 3, 4]);

console.log(random(), random(), random(), random());
//=> 4 2 1 4
```
*/
declare function uniqueRandomArray<ValueType>(
	array: readonly ValueType[]
): () => ValueType;

export = uniqueRandomArray;
